DROP TABLE IF EXISTS films;
CREATE TABLE films (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    image TEXT,
    route TEXT,
    title TEXT NOT NULL,
    rating REAL,
    trailer TEXT,
    agelimit INTEGER,
    description TEXT,
    country TEXT,
    release_year INTEGER,
    add_at TEXT,
    video TEXT
);

DROP TABLE IF EXISTS videos;
CREATE TABLE videos (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    image TEXT,
    route TEXT,
    title TEXT NOT NULL,
    rating REAL,
    trailer TEXT,
    agelimit INTEGER,
    description TEXT,
    country TEXT,
    release_year INTEGER,
    add_at TEXT,
    video TEXT
);

DROP TABLE IF EXISTS serials;
CREATE TABLE serials (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    image TEXT,
    route TEXT,
    title TEXT NOT NULL,
    rating REAL,
    trailer TEXT,
    agelimit INTEGER,
    description TEXT,
    country TEXT,
    release_year INTEGER,
    last_year INTEGER,
    add_at TEXT
);

DROP TABLE IF EXISTS episodes;
CREATE TABLE episodes (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    title TEXT,
    ep INTEGER NOT NULL,
    season INTEGER NOT NULL,
    serial_route INTEGER NOT NULL,
    video TEXT
);

DROP TABLE IF EXISTS genres;
CREATE TABLE genres (
    id INTEGER PRIMARY KEY,
    genre TEXT NOT NULL
);

DROP TABLE IF EXISTS film_genre;
CREATE TABLE film_genre (
    route TEXT NOT NULL,
    genre_id INTEGER NOT NULL
);

DROP TABLE IF EXISTS persona;
CREATE TABLE persona (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    image TEXT,
    route TEXT NOT NULL,
    name TEXT NOT NULL,
    biography TEXT,
    birthday TEXT,
    birthplace TEXT,
    deathday TEXT,
    deathplace TEXT
);

DROP TABLE IF EXISTS film_director;
CREATE TABLE film_director (
    film_route TEXT NOT NULL,
    person_route TEXT NOT NULL
);

DROP TABLE IF EXISTS film_actor;
CREATE TABLE film_actor (
    film_route TEXT NOT NULL,
    person_route TEXT NOT NULL
);

DROP TABLE IF EXISTS comments;
CREATE TABLE comments (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    film_route TEXT NOT NULL,
    username TEXT NOT NULL,
    message TEXT,
    datetime TEXT
);

DROP TABLE IF EXISTS scores;
CREATE TABLE scores (
    film_id INTEGER NOT NULL,
    user_id INTEGER NOT NULL,
    score INTEGER
);
CREATE INDEX scores_index
ON scores (film_id, user_id);

DROP TABLE IF EXISTS users;
CREATE TABLE users (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    name TEXT NOT NULL,
    email TEXT NOT NULL,
    password TEXT NOT NULL
);

DROP TABLE IF EXISTS news;
CREATE TABLE news (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    route TEXT NOT NULL,
    image TEXT,
    title TEXT NOT NULL,
    description TEXT,
    created_date TEXT
);

VALUES ('front_krovavoy_blokady', '2022_04_07-1.webp', '«Фронт кровавой блокады» — аниме для тех, кому нравятся «Люди в черном» и «Стража»','Прошло три года, с тех пор как посреди Нью-Йорка открылся портал в другое измерение. Чтобы оградить весь мир от пришедших из разлома пришельцев, маги обоих миров окружили город волшебным барьером, а оставшимся за ним людям теперь приходится уживаться с монстрами и демонами. В новом мегаполисе, названном Хеллсалемом, за порядком неофициально следит организация «Либра», все члены которой обладают сверхъестественными способностями. Однажды к ним прибивается молодой фотограф Леонард Вотч, недавно переехавший в город. Он кажется самым обычным парнем, но у него есть уникальный дар — видеть то, что скрыто от глаз других людей и существ.<br/><br/>Леонард сталкивается с множеством опасностей и по неопытности постоянно попадает в передряги. Его выручают новые коллеги: задира Запп, который умеет управлять собственной кровью и использовать ее как оружие, девушка-оборотень Чейн, звероподобный лидер организации Клаус фон Райнхерц и многие другие.<br/><br/>Аниме снято по одноименной манге Ясухиро Найто (автор популярного «Тригана») студией Bones. Это один из ведущих японских производителей аниме; культовые «Цельнометаллический алхимик», «Проза бродячих псов» и «Бродячий бог» — это все Bones.<br/><br/>«Фронт кровавой блокады» — отличная точка входа для тех, кто не увлекается аниме или смотрел совсем немного. Зрителю не нужно глубоко разбираться в японской мифологии и поп-культуре. Действие сериала разворачивается в привычных декорациях условного Нью-Йорка, который хорошо знаком всем по фильмам. Поэтому большинство отсылок считываются очень легко: похожий на Шрека монстр в кофейне, постоянно звучащая в домах героев классическая музыка, идущий в летнем кинотеатре фильм «Близнецы» с ДеВито и Шварценеггером и так далее. Время от времени герои даже упоминают фильмы вселенной Marvel! Конечно, без классических японских тропов тут тоже не обошлось, но быстро разобраться в них можно с помощью нашего гида.', '7 апреля' );
VALUES ('9_luchshih_treylerov_nedeli', '2022_04_08-1.webp', '9 лучших трейлеров недели: Мэйси Уильямс становится панк-иконой, а Кейли Куоко знакомится со своими клонами','Мини-сериал Дэнни Бойла («На игле») о панк-группе Sex Pistols. В основу легли мемуары ее основателя и гитариста Стива Джонса (его играет Тоби Уоллес). Действие охватит три года из жизни группы. Сценарий написал Крэйг Пирс («Элвис»). Участников группы сыграли Энсон Бун (Джон Лайдон), Кристиан Лис (Глен Мэтлок) и Луис Патридж (Сид Вишес). В шоу также появятся Мэйси Уильямс («Игра престолов») в роли панк-иконы Памелы Рук, Талула Райли («Мир Дикого Запада») сыграет дизайнера Вивьен Вествуд, а Томас Сэнгстер («Ход королевы») — менеджера группы Малкольма Макларена.<br/><br/>Детективный сериал Amazon. Ройал Эбботт (Джош Бролин) — владелец ранчо в Вайоминге, куда приезжает туристка Отэм (Имоджен Путс), изучающая странные символы на камнях в этой местности. Вскоре после ее приезда Ройал находит странный объект в поле, способный всасывать окружающие объекты.<br/><br/>Продолжение драмеди Netflix с Наташей Лионн. В первом сезоне ее героиня Надя отметила 36-летие, после чего ее насмерть сбила машина. Но Надя не погибла, а оказалась во временной петле, где каждый день отмечала день рождения, а потом умирала разными способами. Во втором сезоне Надя путешествует во времени и оказывается в 1980-х, откуда тоже не может вырваться.<br/><br/>Комедия инди-студии A24 («Всё везде и сразу»). Главный герой — Марсель, антропоморфная морская ракушка в туфельках. Он живет с бабушкой Конни (ее озвучивает Изабелла Росселлини) и хочет найти свою семью, с которой вынужден был расстаться. О Марселе снимают документалку, и историю героя узнает весь мир. В основу легли одноименные ролики Дина Флейшера-Кэмпа и Дженни Слейт («Парки и зоны отдыха»), по которым позже написали детские книги.<br/><br/>После первого сезона бортпроводница Кэсси (Кейли Куоко) пытается наладить жизнь. Если в первом сезоне она проснулась с мертвым мужчиной, непонятно откуда взявшимся, то во втором сама оказывается под угрозой. Между сезонами Кэсси наладила личную жизнь и устроилась в ЦРУ работать под прикрытием. Но тут кто-то создает ее клонов, чтобы обвинить ее в преступлениях, которые она не совершала (а заодно украсть ее жизнь).<br/><br/>Мистический триллер из Южной Кореи. Чхон У-хи («Вопль») играет начинающую телеведущую. Однажды она получает звонок от незнакомой женщины, которая просит рассказать о своей смерти в прямом эфире. Когда выясняется, что незнакомку на самом деле убили, телеведущей везде начинает мерещиться призрак покойной.<br/><br/>Продолжение криминального боевика о буднях корейских полицейских. Действие происходит в 2008 году. Опергруппа под руководством харизматичного начальника в исполнении Ма Дон-сока («Вечные») отправляется со спецзаданием в Таиланд. Там копы должны вычислить и поймать сбежавшего соотечественника, нападающего на корейских туристов.<br/><br/>Колливудский (кино на тамильском языке) боевик в стиле «Крепкого орешка». В торговой центр индийского города Ченнаи врываются террористы и берут посетителей в заложники. Совершенно случайно в этом же молле оказывается шпион (Виджай), который считает делом чести расправиться со злоумышленниками и освободить сограждан.<br/><br/>Камерный постапокалипсис в Гонконге. Женщина выживает в полуразрушенном здании, после того как мир накрыла глобальная природная катастрофа, а город был практически уничтожен гигантским цунами. Ее уединение заканчивается, когда на обломках к ней приплывает маленькая девочка, которая явно от кого-то скрывается.', '8 апреля');
VALUES ('chto_posmotret_standup', '2022_04_12-1.webp', 'Что посмотреть у американских стендап-комиков? Мы выбрали 3 концерта', 'Зарождению зарубежного стендапа и его внутренней кухне посвящали отдельные сериалы («Удивительная миссис Мейзел», «По друзьям», «Хитрости»). Пришло время познакомиться с самими комиками, которые формируют современную американскую комедию:<br/><br/>«Уитни Каммингс: Здравствуйте, я ваша девушка»<br/><br/>«Дрю Майкл»<br/><br/>«Ивонн Орджи: Мама, я это сделала!»', '12 апреля' );
VALUES ('chto_pokazali_strannye_dela', '2022_04_13-1.webp', 'Что показали в новом трейлере «Очень странных дел»?', 'Осторожно: спойлеры! После того как коммунисты снова открыли врата в Изнанку, Истязатель Разума создал в Хоукинсе девятиметрового паукообразного монстра, который охотился за Одиннадцатой (Милли Бобби Браун) и ее друзьями. Кусая людей, он пополнял свою армию, в которую попал сводный брат Макс, Билли (Дэйкер Монтгомери). После укуса люди начинали подчиняться монстру и приводили ему новых жертв.<br/><br/>В День независимости, когда все жители Хоукинса собрались на ярмарке, монстр вышел наружу. Чтобы уничтожить его, героям нужно было добраться до подземной лаборатории, где снова пытались открыть врата. Оказалось, что ее построили коммунисты (вместе с ученым Алексеем, которого сыграл Алек Утгофф) прямо под местным торговым центром «Старкорт». В лабораторию спустились шериф Хоппер (Дэвид Харбор), Джойс Байерс (Вайнона Райдер) и детектив Мюррей Бауман (Бретт Гельман). А дети боролись с монстром на поверхности. Он укусил Одиннадцатую за ногу, оставив там свой отросток. Чтобы извлечь его, Одиннадцатая потратила слишко много сил и больше не могла использовать свои сверхъестественные способности. Джойс остановила машину, открывающую врата в Изнанку, пожертвовав Хоппером (он находился у врат).<br/><br/>Спустя три месяца Робин (Майя Хоук) и Стив (Джо Кири) устроились в кинопрокат после кафе-мороженого. Джойс продала дом и переехала вместе с Уиллом (Ноа Шнапп), Джонатаном (Чарли Хитон) и Одиннадцатой. К ней так и не вернулись ее способности, поэтому она не могла узнать, что Хоппер выжил, но оказался в трудовом лагере в СССР.', '13 апреля' );
VALUES ('eto_ne_prosto_bolezn', '2022_04_14-1.webp', '«Это не просто болезнь»: вышел тизер сериала «Нулевой пациент» о вспышке ВИЧ в СССР', 'Производством сериала «Нулевой пациент» занимается продюсерская компания «Среда». Он посвящен событиям 1988 года, когда в детской больнице в Калмыкии произошла первая крупная вспышка ВИЧ в СССР.<br/><br/>Молодой врач Кирсан Аюшев (Аскар Ильясов) подозревает, что у нескольких его пациентов ВИЧ. Старшие коллеги не верят, что такое возможно, поэтому Кирсан тайком отправляет пробы в Москву. Когда анализы подтверждают его правоту, к делу подключается ученый Дмитрий Гончаров (Никита Ефремов), специализирующийся по этой пока малоизвестной в СССР болезни.<br/><br/>Премьера «Нулевого пациента» состоится весной на Кинопоиске, точная дата пока неизвестна.', '14 апреля' );
VALUES ('kak_snimaut_mayora_groma', '2022_04_15-1-1.webp', 'Как снимают приквел «Майора Грома»? Мы расспросили режиссера Олега Трофима', 'В Санкт-Петербурге идут съемки фильма «Майор Гром: Трудное детство» — приквела прошлогоднего блокбастера «Майор Гром: Чумной Доктор» о детстве Игоря Грома. Действие разворачивается за 20 лет до событий «Чумного доктора» в альтернативном Санкт-Петербурге. Майор полиции Константин Гром пытается найти и обезвредить нового босса криминального мира по прозвищу Анубис, в то время как его десятилетний сын Игорь пробует заработать деньги, ввязываясь в сомнительные авантюры.<br/><br/>Съемки проходят в павильонах «Ленфильма», на Исаакиевской площади и в Мраморном дворце. Фильм выйдет в новогодние каникулы 2023 года только на Кинопоиске. Режиссером вновь выступает Олег Трофим, поставивший «Чумного Доктора».', '15 апреля' );
VALUES ('v_kannah_serebrennikov', '2022_04_15-1-2.webp', 'Канны объявили программу. В основной конкурс снова вошел фильм Кирилла Серебренникова', 'Организаторы Каннского фестиваля объявили программу 75-го смотра. Он пройдет с 17 по 28 мая. Фильмом открытия станет комедия о зомби «Окончательный монтаж» Мишеля Хазанавичуса.<br/><br/>75-й Каннский кинофестиваль пройдет с 17 по 28 мая 2022 года.<br/><br/>Ранее стало известно, что новым президентом фестиваля избрали Айрис Кноблох, которая возглавляла Warner Bros. во Франции. Она сменит на данном посту Пьера Лескура и станет первой женщиной на этой должности. Кноблох будет руководить киносмотром до 2025 года.', '15 апреля');
VALUES ('treylery_nedeli', '2022_04_15-1-3.webp', 'Лучшие трейлеры недели: Милли Бобби Браун встречает нового монстра, а Никита Ефремов помогает детям', 'Предпоследний сезон хита Netflix о школьниках из города Хоукинс, которые сталкиваются с потусторонними монстрами. В четвертом сезоне ребята столкнутся с человекообразным монстром из Изнанки — Векной. Еще героям предстоит вернуть шерифа Хоппера (Дэвид Харбор) из тюрьмы на Камчатке, разобраться с прошлым Одиннадцатой (Милли Бобби Браун) и зловещим домом пациента психбольницы Виктора Крила, который как-то связан с Изнанкой.<br/><br/>Сериал о первой крупной вспышке ВИЧ в СССР, которая произошла в 1988 году в детской больнице Калмыкии. Врач Кирсан Аюшев (Аскар Ильясов) подозревает, что у нескольких его пациентов ВИЧ. Старшие коллеги ему не верят, поэтому Кирсан тайком отправляет пробы в Москву. Когда анализы подтверждают его правоту, к делу подключается ученый Дмитрий Гончаров (Никита Ефремов), специализирующийся на малоизвестной в СССР болезни. Режиссерами сериала выступили Сергей Трофимов («Девятаев») и Евгений Стычкин («Контакт»), продюсерами — Александр Цекало, Иван Самохвалов, Олег Маловичко, Александра Ремизова и Ольга Филипук. Сценарист — Олег Маловичко («Метод», «Хрустальный»).<br/><br/>Средневековый эпос Роберта Эггерса («Маяк»), основанный на скандинавских легендах о принце Амлете. X век, Исландия. На глазах юного Амлета дядя Фьёльнир (Клас Банг) убивает его отца-короля (Итан Хоук). Мальчик сбегает из дома, поклявшись отомстить. Амлет попадает в плен викингов, где знакомится с ведьмой Ольгой (Аня Тейлор-Джой). Вместе они сбегают и возвращаются на родину Амлета, чтобы убить его дядю и освободить мать (Николь Кидман).', '15 апреля' );
VALUES ('chto_v_prokate', '2022_04_19-1.webp', 'Что происходит в прокате: в России сборы бьют антирекорды, в США проваливаются «Фантастические твари»', 'Российские кинотеатры продолжают стоять полупустыми без мейджорского голливудского контента. В прошедший уик-энд общая касса всех идущих в прокате фильмов составила 220 млн рублей. Это худший результат за последние пять лет. Например, год назад аналогичные апрельские выходные при ковидных ограничениях (50-процентная заполняемость залов) принесли кинотеатрам 522 млн рублей. Глава сети «С5» Алексей Воронков заявил, что в марте перестали функционировать 25% залов. Компания «Невафильм Research» приводит другие данные: за месяц количество залов в стране уменьшилось на 7% (с 5635 до 5248), при этом количество самих кинотеатров осталось прежним. Объясняется это тем, что мультиплексы консервируют часть залов, а оставшиеся продолжают работать с текущим репертуаром, скудным на кассовые хиты.<br/><br/>Новости зарубежного проката чем дальше, тем больше напоминают вестник параллельной реальности. Так, у боссов студии Sony в последние недели мало поводов для радости. Кинокомикс «Морбиус», на который возлагались большие надежды, стартовал в два раза хуже первого «Венома». Но и за три недели общая касса проекта в США составляет всего 65 млн долларов. Зарубежные рынки принесли еще 81,3 млн долларов. При бюджете в 75 млн долларов картину, скорее, уже можно считать убыточной. Не все гладко и у триквела «Фантастических тварей». «Тайны Дамблдора» стартовали в США на прошлой неделе и собрали в премьерный уик-энд 43 млн долларов. Это на 42% хуже старта оригинального фильма и на 30% меньше, чем у сиквела. За пределами США фильм Warner Bros. работает уже две недели. За это время он заработал на международной арене 71,7 млн долларов. Отставание от общих сборов сиквела составляет 38%.', '19 апреля');
VALUES ('devochki_10_let', '2022_04_20-1.webp', 'Сериалу «Девочки» — 10 лет. Как сложилась карьера актеров, сыгравших его главных героев?', 'С момента выхода сериала «Девочки» прошло 10 лет (премьера первой серии состоялась в апреле 2012-го на HBO). Драма о четырех 20-летних подругах, живущих в Нью-Йорке, с Линой Данэм, которая была и актрисой, и шоураннеркой, полностью перевернула представление о молодых женщинах на экране. В отличие от своего глянцевого предшественника «Секс в большом городе», четыре главные героини «Девочек» были неловкими, порой раздражающими и обнадеживающе человечными. Сериал произвел революцию на телевидении, получил 19 номинаций на «Эмми», а Данэм стала голосом поколения.', '20 апреля' );
VALUES ('chto_proishodit_lunniy_rycar', '2022_04_21-1.webp', 'Что происходит в новых сериях «Лунного рыцаря»', 'Марк Спектор (Оскар Айзек) прыгает по каирским крышам как заправский ассасин и дерется с местными бандитами. Он хочет узнать, где прячется Артур Хэрроу (Итан Хоук), намеревающийся вернуть к жизни египетскую богиню Амат. Во время драки Марк несколько раз меняется местами со Стивеном, но остается загадкой, кто из них жестоко убил бандитов (обе личности не берут ответственность за преступление). Также в Каир прибывает Лейла (Мэй Каламави).<br/><br/>Тем временем Хонсу устраивает солнечное затмение, чтобы созвать совет богов внутри пирамиды Хеопса. Причина — суд над Артуром Хэрроу. Но Хатхор, Гор, Исида, Тефнут и Осирис не верят, что смертный человек и бывший аватар Хонсу может решиться освободить Амат, которую заточили в камне. И даже угрожают лунному богу заточением в камень, если тот продолжит вмешиваться в дела людей.<br/><br/>Марк и Лейла выходят на коллекционера древностей Антона Могарта (Гаспар Ульель), у которого есть артефакт с указанием местоположения гробницы Амат. Вскоре к ним присоединяется Хэрроу, и начинается перестрелка. Там Марк передает тело Стивену, а Хонсу использует свои силы, чтобы подсказать им путь к гробнице, в итоге другие боги исполняют угрозу и превращают его в каменную статуэтку. Теперь у главного героя нет возможности использовать силы своего покровителя и вызывать супергеройский костюм.', '21 апреля' );
VALUES ('volchica_pryanosti', '2022_04_22-1.webp', '«Волчица и пряности» — аниме для тех, кому не хватает тепла', 'Много веков крестьяне за помощью с урожаем обращались к богине-волчице по имени Хоро. Но время шло, технологии совершенствовались, и они перестали нуждаться в благословении. Хоро затосковала и решила вернуться в родные края — Северные земли. Обратившись девушкой, она поселилась в повозке странствующего торговца Крафта Лоуренса и попросила его помочь ей добраться домой, пообещав свою помощь в торговых сделках. После раздумий Лоуренс соглашается помочь богине, и вместе они отправляются в долгое путешествие, в котором их ждут встречи с банкирами, бандитами, менялами, церковниками и законниками.<br/><br/>В отличие от большинства аниме в жанре фэнтези, персонажи «Волчицы» не владеют мечом и магией, и в фокусе тут не битвы, а торговля и экономика. Лоуренс общается с землевладельцами, объясняет систему бартера, рассказывает Хоро о системе судоперевозок и устройстве торговых компаний. Целая сюжетная арка тут может строиться не на убийстве злодея, а на раскрытии махинаций с валютой. При этом рассказы Лоуренса не воспринимаются как скучные лекции по экономике Средневековья. Он и сам искренне любит свое дело и рассказывает о нем увлеченно и просто, чтобы понятно было и зрителям, и Хоро, которая никогда до встречи с ним даже не пользовалась деньгами.<br/><br/>Главных героев тут всего двое, и драматургия строится исключительно на диалогах Лоуренса и Хоро. А остальные герои появляются ненадолго и служат, скорее, фоном для развития отношений. Во-вторых, ни Лоуренс, ни Хоро не похожи на типичных героев романтических аниме вроде «Корзинки фруктов» или «Хост-клуба Оранской школы». Лоуренс — редкий герой в аниме, взрослый человек, у которого есть знания, цели и обязательства.', '22 апреля' );


VALUES ('Матрица', 'matrix', 'Фильм изображает будущее, в котором реальность, существующая для большинства людей, в действительности является симуляцией, созданной разумными машинами, чтобы подчинить и усмирить человечество, в то время как тепло и электрическая активность их тел используются машинами в качестве источника энергии. Немногие люди, высвободившиеся из «мира снов» и выбравшиеся в реальность, вступают в партизанскую войну против машин…', 1999, 'США, Австралия', 16, 'matrica.jpeg', 9.3, 'https://www.film.ru/sites/default/files/trailers/1622170/Matrix-TrailerRus.mp4', '2022-05-29 11:45:00', 'http://87.229.223.234/%d0%9c%d0%b0%d1%82%d1%80%d0%b8%d1%86%d0%b0.mp4');
VALUES ('Матрица: Воскрешение', 'matrix-reborn', 'Четвёртый фильм про Матрицу. Начало картины отсылает к вступительной сцене первого фильма: агенты гонятся за молодой Тринити, но… что-то идёт не так, и Тринити задерживают. Томас Андерсон — всемирно известный геймдизайнер, проживающий в Сан-Франциско. В своё время он руководил разработкой трилогии видеоигр «Матрица». Ныне его фирма Deus Machina приобретена корпорацией Warner Bros. Новые владельцы распоряжаются перезапустить проект и выпустить продолжение. Томас неохотно включается в работу, его мучают видения и кошмары, словно бы мир, в котором он живёт, нереален. Он посещает психоаналитика, который выписывает ему некие синие пилюли. В любимой кофейне Simulatte Томас встречает Тиффани, которая в итоге рассказывает Андерсону, что он находится в виртуальном мире, созданном искусственным интеллектом, в Матрице, и что его зовут Нео. Со времени последней версии Матрицы, той где Нео спас жителей Зиона, прошло 60 лет…', 2021, 'США', 16, 'matrix_resurrection.jpg', 9.0,'https://www.film.ru/sites/default/files/trailers/47996050/The_Matrix_Resurrections_-_The_Game_Awards_Clip_0.mp4', '2022-05-29 11:46:00', 'http://87.229.223.234/The.Matrix.2021.DUB.WEB-DLRip.x264.mkv');
VALUES ('Человек-паук', 'spiderman', 'Первый фильм трилогии Сэма Рэйми. Основан на истории одноимённого персонажа Marvel Comics. Фильм повествует о жизни Питера Паркера, студента старшей школы из Нью-Йорка, о получении им суперспособностей и первых шагах в качестве супергероя…', 2002, 'США', 16, 'chel.jpeg', 8.5, 'https://www.film.ru/sites/default/files/trailers/1610289/Spider-Man-First-Time.mp4', '2022-05-29 14:05:00', 'http://87.229.223.234/Spider-man.2002_HDRip__Kinosimka.RU.mp4');
VALUES ('Гарри Поттер и узник Азкабана', 'hurrypotter_azkaban', 'Третий фильм серии о Гарри Поттере, экранизация одноименного романа Джоан Роулинг, третьего в серии её книг. Во время описываемых событий Гарри Поттер на третьем курсе школы чародейства и волшебства Хогвартс. Вместе со своими друзьями Роном Уизли и Гермионой Грейнджер узнает историю Сириуса Блэка — бежавшего из тюрьмы Азкабан волшебника, который подозревается в работе на лорда Волан-де-Морта', 2004, 'США, Великобритания', 6, 'garri.jpeg', 8.6,'https://www.film.ru/sites/default/files/trailers/1610780/Willow.mp4','2022-05-29 14:10:00', 'http://87.229.223.234/%d0%93%d0%b0%d1%80%d1%80%d0%b8_%d0%9f%d0%be%d1%82%d1%82%d0%b5%d1%80_3__%d0%a3%d0%b7%d0%bd%d0%b8%d0%ba_%d0%90%d0%b7%d0%ba%d0%b0%d0%b1%d0%b0%d0%bd%d0%b0.mp4');
VALUES ('Аватар', 'avatar', 'Действие фильма разворачивается в 2154 году, когда человечество добывает ценный минерал анобтаниум на Пандоре, обитаемом спутнике газовой планеты в звёздной системе Альфы Центавра. По сюжету ресурсодобывающая корпорация угрожает существованию местного племени человекоподобных разумных существ — на’ви. Команда исследователей планеты использует аватары — генетически спроектированные тела, гибриды на’ви и людей…', 2009, 'США', 18, 'avatar.jpeg', 8.0,'https://www.film.ru/sites/default/files/trailers/1627918/Avatar-Trailer.mp4', '2022-05-29 14:15:00', 'http://87.229.223.234/Avatar_2009_720_kinosimka.pro.mp4');
VALUES ('Белоснежка и охотник', 'snowwhite_hunter', 'Фильм снят по мотивам сказки братьев Гримм. В королевство Табор вторгается армия стеклянных солдат. Король Магнус разбивает врагов, освобождает прекрасную пленницу Равенну и женится на ней, но в первую же ночь Равенна убивает короля, открывает ворота замка перед врагом и захватывает власть над королевством. Она оказывается злой колдуньей, поддерживающей свою вечную юность высасыванием жизненных сил девушек…', 2012, 'США', 16, 'belo.jpeg', 8.0,'https://www.film.ru/sites/default/files/trailers/1629721/Snow-White-and-the-Huntsman-Trailer-rus.mp4', '2022-05-12 17:30:36');
VALUES ('Эверест', 'everest', 'В основе фильма лежат реальные события, произошедшие в Гималаях в мае 1996 года. Группа альпинистов-любителей, участников экспедиции «Консультанты по приключениям» под руководством профессионального гида Роба Холла и его помощников отправляется на покорение высочайшей вершины мира, но встречается с экспедицией «Горное безумие». Скопление людей и приближающаяся буря приводят к трагедии…', 2015, 'США, Великобритания', 18, 'everest.jpeg', 7.2,'https://www.film.ru/sites/default/files/trailers/3074966/Everest-Trailer-rus.mp4', '2022-05-29 14:30:36', 'http://87.229.223.234/Everest.2015_HDRip_kinosimka.pro.mp4');
VALUES ('Криминальное чтиво', 'kriminalnoe-chtivo', 'Знаменитая чёрная комедия Квентина Тарантино, ставшая шедевром благодаря присущей Тарантино нелинейности сюжета. Пример постмодернистского кино, настоящее культурное явление. История разделена на части, которые показаны зрителю в неправильном порядке. Бандиты Винсента Вега и Джулс Уинфилд должны забрать принадлежащий их боссу чемодан…', 1994, 'США', 18, 'crime.jpg', 'https://www.film.ru/sites/default/files/trailers/1612526/Pulp%20Fiction%20Trailer%20Rus.mp4', '2022-05-13 17:56:25');
VALUES ('Джеки', 'jackie', 'Жаклин Кеннеди, «Джеки» или Жаклин О, как ее еще называли современники, была первой леди США с 1961 по 1963 год. Одна из самых популярных женщин своего времени, законодательница моды, красоты и изящества в Америке и Западной Европе, героиня светской хроники. Фильм повествует о событиях, которые разворачивается в течение четырёх трагических ноябрьских дней после убийства президента Джона Кеннеди…', 2016, 'США', 18, 'jackie.jpg', 6.6, 'https://www.film.ru/sites/default/files/trailers/20169294/jackie.mp4', '2022-05-19 17:46:55');
VALUES ('Повелитель бури','hurtlocker','2004 год, идёт второй год войны в Ираке. Элитное подразделение сапёров армии США работает в охваченном партизанской войной Багдаде: каждый встречный – потенциальный враг, каждый предмет может быть замаскированной бомбой. При обезвреживании самодельного взрывного устройства гибнет сержант Томпсон. На замену ему прибывает опытный сапёр Уильям Джеймс. Отношения между Джеймсом и группой изначально не задаются…', 2008, 'США', 16, 'storm_lord.jpg', 7.5, 'https://www.film.ru/sites/default/files/trailers/1627638/The-Hurt-Locker-Trailer.mp4', '2022-05-13 17:56:25');

VALUES ('Как Уилл Смит пришел к Оскару', 'how_will_smith_came_to_oscar', 'С чего Уилл Смит начинал карьеру в шоу-бизнесе? Как он справлялся с провалами? Почему Смит не просто суперзвезда Голливуда, но и важная фигура в общественной жизни США? И какие культовые роли он мог сыграть, но отказался?', 2022, 'США', 6, 'smit.jpg', 5.5, 'https://www.film.ru/sites/default/files/trailers/4221101/A-Mans-Story-Trailer.mp4', '2022-05-12 17:30:36', 'http://87.229.223.234/kak-uill-smit-prisel-k-oskaru_(videomega.ru).mp4');
VALUES ('Рецензия на фильм «Прожигатели жизни»','review_prozhigateli','Милейшее кино без всяких закидонов, не «ах», но и не «на любителя», «Прожигатели жизни» (2006) напрасно названы кинокомедией. Одна из старейшин нашего цеха Ирина Михайловна Шилова до сих пор утверждает, что единственным преимуществом советского кино был жанр, которого больше не было нигде — «киноповесть». И в этом плане Майк Байндер - вполне советский режиссер. Присутствие звезд, отсутствие спецэффектов, нормальные диалоги, ровный нескучный ритм и как бы мерцающую в каждом кадре иронию над Америкой он действительно свел к вполне законченному жанру.<br/>Киноповести не получилось бы у Спилберга, поначалу запавшего на байндеровский сценарий. Был бы очередной плакатный «Терминал» (2004) с «эффектной» слезливостью, и ничего смешного. Но, хотя Том Хэнкс – актер получше Бена Эффлека, об которого только ленивый еще не вытер ноги, он в этом недорогом кино ничего для себя не открыл бы. А столь дорогостоящий Эффлек открыл, что хотя бы способен вписываться в ансамбль.', 2006, NULL, 16, 'review_prozhigateli.jpg', 'https://www.film.ru/sites/default/files/trailers/1611653/Man-About-Town.mp4', '2022-05-12 11:30:30');

VALUES ('Игра престолов','game_of_thrones','Действие «Игры престолов», основанный на цикле романов «Песнь льда и огня» Джорджа Р. Р. Мартина, происходит в вымышленном мире, напоминающем средневековую Европу. В самом масштабном и обсуждаемом телесериале 2010-х одновременно развиваются три основные истории: борьба нескольких влиятельных домов за Железный Трон Семи Королевств либо за независимость от него; история потомка свергнутой династии правителей, принцессы-изгнанницы, планирующей вернуть престол; сага о древнем братстве, охраняющем государство от угроз с севера. В сериале поднимаются вопросы насилия и гражданской войны, положения женщин и сексуальных меньшинств в обществе, роли Церкви и семьи в системе социальных отношений…', 2011, 2019, 'США, Великобритания', 18, 'got.jpg', 9.0, 'https://www.film.ru/sites/default/files/trailers/1631897/trailer.mp4', '2022-06-04 08:30:00');
VALUES ('Доктор Хаус','doctor_house', 'Сериал повествует о выдающемся враче-диагносте Грегори Хаусе и его команде, являясь одним из самых популярных произведений в жанре медицинский детектив. Главный герой замкнут, резок и циничен, при этом склонен к бунтарству и не обременяет себя соблюдением правил хорошего тона. Любимое утверждение доктора — «Все лгут». Однако, такого доктора не проведёшь…', 2004, 2012, 'США', 16, 'doctor_house.webp', 7.3, 'https://www.film.ru/sites/default/files/trailers/1627265/House-M-D-Trailer.mp4', '2022-06-04 08:40:00');

VALUES ('Киану Ривз', 'keanu_reeves', 'a_keanu_reeves.webp', '1964-09-02', 'Бейрут, Ливан', 'Полное имя — Киану Чарльз Ривз (Keanu Charles Reeves). По-гавайски имя Keanu означает «прохладный ветер над горами». Отец Киану — Сэмуэль Ноулин Ривз — наполовину гаваец, наполовину китаец, а мать — Патрисия Тэйлор — англичанка. Киану — левша. У Киану никогда не было собственного компьютера. Если ему приходится все же общаться с компьютером — он играет в шахматы. Киану не раз утверждал, что его больше интересуют отношения человека и компьютера, нежели сам компьютер как таковой. Киану боится иметь детей, потому что этот мир его часто пугает. Однако он считает, что способен влюбиться до такой степени, когда уже станет все равно, что тебя ждет впереди.');
VALUES ('Лоренс Фишбёрн','laurence_fishburne','a_laurence_fishburne.webp','1961-07-30','Огаста, Джорджия, США','Полное имя — Лоренс Фишбёрн III (Laurence J. Fishburne III). Лоренс — левша.');
VALUES ('Кэрри-Энн Мосс', 'carrie-anne_moss', 'a_carrie-anne_moss.webp', '1967-08-21', 'Ванкувер, Британская Колумбия, Канада', 'По семейной легенде, она получила свое имя в честь песни группы «Hollies» «Carrie Anne», которая была большим хитом в год ее рождения. Перед тем как стать актрисой, Мосс несколько лет работала официанткой, а потом — моделью. У Кэрри-Энн Мосс есть трое детей от брака со Стивеном Ройем, два сына и дочь — Оуэн Рой (род. в сентябре 2003), Джейден Рой (род. в ноябре 2005) и Фрэнсис Беатрис Рой (род. в июне 2009).');
VALUES ('Хьюго Уивинг', 'hugo_weaving', 'a_hugo_weaving.webp', '1960-04-04', 'Ибадан, Нигерия', 'Полное имя — Хьюго Уоллес Уивинг. Высшее образование получал в Национальном Институте Драматического Искусства Австралии. Закончил его в 1981 году. Пока Хьюго учился в институте, некоторое время он подрабатывал в порту, перетаскивая мешки с кофе.');
